import React from 'react';
import { FlatList, View, Text, StyleSheet, Image } from 'react-native';

const genres = [
  { name: 'Suspense', image: { uri: 'data:image/jpeg;base64,' } },
  { name: 'Romance', image: { uri: 'data:image/jpeg;base64,' } },
  { name: 'Ficção Cientifica', image: { uri: 'data:image/jpeg;base64,' } },
  { name: 'Fantasia', image: { uri: 'data:image/jpeg;base64,' } },
];

const Carousel = () => {
  return (
    <FlatList
      data={genres}
      horizontal
      showsHorizontalScrollIndicator={false}
      keyExtractor={(item, index) => index.toString()}
      contentContainerStyle={styles.carouselContainer}
      renderItem={({ item }) => (
        <View style={styles.genreItem}>
          <Image source={item.image} style={styles.genreImage} />
          <Text style={styles.genreText}>{item.name}</Text>
        </View>
      )}
    />
  );
};

const styles = StyleSheet.create({
  carouselContainer: {
    paddingHorizontal: 10,
  },
  genreItem: {
    width: 80,  // Ajuste a largura do card
    height: 80, // Ajuste a altura do card
    justifyContent: 'center',
    alignItems: 'center',
    marginRight: 10,
    borderRadius: 20,  // Radius para bordas arredondadas
    overflow: 'hidden',  // Isso ajuda a garantir que a imagem não ultrapasse os limites do card
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.2,
    shadowRadius: 4,
    elevation: 5, // Para dar um leve destaque no Android
  },
  genreImage: {
    width: '100%',  // Faz com que a imagem ocupe toda a largura do card
    height: '100%', // Faz com que a imagem ocupe toda a altura do card
    resizeMode: 'cover',  // Faz a imagem cobrir todo o espaço sem distorcer
  },
  genreText: {
    position: 'absolute', // Coloca o texto sobre a imagem
    bottom: 10,  // Ajuste a posição para onde você quer o texto
    textAlign: 'center',
    color: '#fff',  // Torna o texto visível sobre a imagem
    fontWeight: 'bold',
    fontSize: 14,
  },
});


export default Carousel;
